<?php

namespace AppBundle\Repository;

/**
 * PlanRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PlanRepository extends \Doctrine\ORM\EntityRepository
{
    public function findBySessionIdRange($user, $from, $to)
    {
        return $this->createQueryBuilder('t')
            ->select('t')
            ->where('t.user = :user')
            ->andWhere('t.sessionId >= :from')
            ->andWhere('t.sessionId <= :to')
            ->setParameter(':user', $user)
            ->setParameter(':from', $from)
            ->setParameter(':to', $to)
            ->addOrderBy('t.sessionId')
            ->getQuery()->getResult();
    }
    public function findBySessionIdsAndUser($list, $user) {
        $qb = $this->createQueryBuilder('t')
            ->select('t')
            ->where('t.sessionId in (:list)')
            ->andWhere('t.user = :user')
            ->setParameter(':list', $list)
            ->setParameter(':user', $user);

        return $qb->getQuery()->getResult();
    }
}
